import React, { useState } from 'react';

const InputField = ({ value, onChange }) => {
  const [isFocused, setIsFocused] = useState(false);

  const handleFocus = () => {
    setIsFocused(true);
  };

  const handleBlur = () => {
    setIsFocused(false);
  };

  return (
    <>
      <input
        className='opacity-0 absolute'
        type="text"
        value={value}
        onChange={onChange}
        onBlur={handleFocus}
        onFocus={handleFocus} 
        onBlur={handleBlur} // Обработчик потери фокуса
        autoFocus
        maxLength={null}
      />
      <div 
        className="cursor-pointer" 
        onClick={handleFocus} // Устанавливаем фокус при клике
        style={{ 
          display: isFocused ? 'none' : 'block', // Скрываем сообщение, если поле в фокусе
          padding: '10px',
          border: '1px dashed #ccc',
          borderRadius: '5px',
          textAlign: 'center',
          marginTop: '10px'
        }}
      >
        Нажмите сюда, чтобы начать писать
      </div>
    </>
  );
};

export default InputField;
