// src/components/TypingText.js
import React from 'react';

const TypingText = ({ text, input }) => {
  return (
    <div className='font-bold w-[60%] text-justify text-4xl'>
      {text.split('').map((char, index) => {
        let color = '#333333';
        if (input[index] === char) {
          color = 'green'; // правильный символ
        } else if (input[index] !== undefined) {
          color = 'red'; // неправильный символ
        }
        return (
          <span key={index} style={{ color }}>
            {char}
          </span>
        );
      })}
    </div>
  );
};

export default TypingText;
