// src/pages/Home.js
import { useState, useEffect } from "react";
import useTypingSpeed from "../hooks/useTypingSpeed";
import TypingText from "../components/TypingText";
import InputField from "../components/InputField";
import Results from "../components/Results";

export const text_example = 
  `Это тестовый текст для ввода. Проверка скорости печати.
  React - это библиотека для создания пользовательских интерфейсов.
  Typing Speed Trainer - отличное приложение для тренировки.`;

const Home = () => {
  const [input, setInput] = useState('');
  const [startTime, setStartTime] = useState(null);
  const [completed, setCompleted] = useState(false);

  const { wpm, errors, timeElapsed } = useTypingSpeed(input, startTime, text_example);

  const handleChange = (e) => {
    console.log('Event:', e);
    if (!e || !e.target) {
      console.warn('Event or target is undefined');
      return;
    }
    if (!startTime) setStartTime(Date.now());
    if (completed) {
      setInput('');
      setStartTime(null);
      setCompleted(false);
      return;
    }
    setInput(e.target.value);
  };

  useEffect(() => {
    if (input.length === text_example.length) {
      setCompleted(true);
    }
  }, [input]);

  return (
    <div>
      <h1>Typing Speed Trainer</h1>
      <TypingText text={text_example} input={input} />
      <InputField value={input} onChange={handleChange} />
      {completed && <Results wpm={wpm} errors={errors} />}
    </div>
  )
}

export default Home;
