import { useState } from "react";
import useTypingSpeed from "../hooks/useTypingSpeed";
import TypingText from "../components/TypingText";
import InputField from "../components/InputField";
import Results from "../components/Results";

export const text =
  `Это тестовый текст для ввода.Проверка скорости печати.
  React - это библиотека для создания пользовательских интерфейсов.
  Typing Speed Trainer - отличное приложение для тренировки.`


const Home = () => {
  const [input, setInput] = useState('');
  const [startTime, setStartTime] = useState(null);

  const { wpm, errors } = useTypingSpeed(input, startTime);

  const handleChange = (e) => {
    if (!startTime) setStartTime(Date.now());
    setInput(e.target.value);
  };

  return (
    <div>
      <h1>Typing Speed Trainer</h1>
      <TypingText text={randomText} input={input} />
      <InputField value={input} onChange={handleChange} />
      {input.length === randomText.length && (
        <Results wpm={wpm} errors={errors} />
      )}
    </div>
  )
}

export default Home
