import React from 'react';

const TypingText = ({ text, input }) => {
  return (
    <div className='font-bold w-[80%] text-justify text-4xl'>
      {text.split('').map((char, index) => {
        let color = '#adadad';
        if (input[index] === char) {
          color = 'green'; // правильный символ
        } else if (input[index] !== undefined) {
          color = 'red'; // неправильный символ
        }

        // Вставляем курсор после текущего символа, если это конец введенного текста
        return (
          <React.Fragment key={index}>
            <span style={{ color }}>{char}</span>
            {index === input.length - 1 && input.length !== text.length && (
              <span className="blinking-cursor">|</span>
            )}
          </React.Fragment>
        );
      })}
      
      {/* Если пользователь еще ничего не ввел, отображаем курсор в начале */}
      {input.length === 0 && <span className="blinking-cursor">|</span>}

      {/* Если пользователь ввел весь текст, но курсор еще не отображается */}
      {input.length === text.length && (
        <span className="blinking-cursor">|</span>
      )}
    </div>
  );
};

export default TypingText;
