// src/hooks/useTypingSpeed.js
import { useState, useEffect } from 'react';
import { texts } from '../pages/Home';

const useTypingSpeed = (input, startTime) => {
  const [wpm, setWpm] = useState(0);
  const [errors, setErrors] = useState(0);

  useEffect(() => {
    if (input.length > 0) {
      const timeElapsed = (Date.now() - startTime) / 60000; // время в минутах
      const wordsTyped = input.split(' ').length;
      setWpm(Math.round(wordsTyped / timeElapsed));
      setErrors(input.split('').filter((char, index) => char !== texts[index]).length);
    }
  }, [input, startTime]);

  return { wpm, errors };
};

export default useTypingSpeed;
