import { useState, useEffect } from "react";
import useTypingSpeed from "../hooks/useTypingSpeed";
import TypingText from "../components/TypingText";
import InputField from "../components/InputField";
import Results from "../components/Results";

// Массив текстов для выбора
const texts = [
  `Это тестовый текст для ввода. Проверка скорости печати. Это тестовый текст для ввода. Проверка скорости печати. Это тестовый текст для ввода. Проверка скорости печати. Это тестовый текст для ввода. Проверка скорости печати.`,
  `React - это библиотека для создания пользовательских интерфейсов. React - это библиотека для создания пользовательских интерфейсов. React - это библиотека для создания пользовательских интерфейсов.React - это библиотека для создания пользовательских интерфейсов.React - это библиотека для создания пользовательских интерфейсов.`,
  `Typing Speed Trainer - отличное приложение для тренировки . Typing Speed Trainer - отличное приложение для тренировки.  Typing Speed Trainer - отличное приложение для тренировки. Typing Speed Trainer - отличное приложение для тренировки. Typing Speed Trainer - отличное приложение для тренировки.`,
];

const Home = () => {
  const [input, setInput] = useState('');
  const [startTime, setStartTime] = useState(null);
  const [endTime, setEndTime] = useState(null);
  const [isCompleted, setIsCompleted] = useState(false);

  const [wpm, setWpm] = useState(0);
  const [errors, setErrors] = useState(0);

  const [currentText, setCurrentText] = useState(texts[0]); // Текущий текст
  // const { wpm, errors, isTimeUp, time } = useTypingSpeed(input, startTime, currentText);

  const handleChange = (e) => {
    if (!startTime) setStartTime(Date.now());
    if (isCompleted) {
      resetGame();
      return;
    }
    setInput(e.target.value);
  };

  const resetGame = () => {
    setInput('');
    setStartTime(null);
    setIsCompleted(false);
    setCurrentText(texts[Math.floor(Math.random() * texts.length)]); // Выбор нового текста
  };

  useEffect(() => {
    if (startTime) {
      const interval = setInterval(() => {
        const time = (Date.now() - startTime) / 1000; // время в секундах
        setTime(elapsed)
        if (elapsed >= 30) {
          setIsTimeUp(true);
          clearInterval(interval);
        }
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [startTime]);

  useEffect(() => {
    if (input.length === currentText.length) {
      setIsCompleted(true);
    }
  }, [input, currentText]);

  return (
    <div>
      <h1>Typing Speed Trainer</h1>
      <TypingText text={currentText} input={input} />
      <InputField value={input} onChange={handleChange} />
      <p>{time}</p>
      {completed && <Results wpm={wpm} errors={errors} />}
      <button onClick={resetGame}>Перезапустить</button> {/* Кнопка для перезапуска */}
    </div>
  );
};

export default Home;
