// src/hooks/useTypingSpeed.js
import { useState, useEffect } from 'react';

const useTypingSpeed = (input, startTime, text) => {
  const [wpm, setWpm] = useState(0);
  const [errors, setErrors] = useState(0);

  useEffect(() => {
    if (startTime) {
      const interval = setInterval(() => {
        setTimeElapsed((Date.now() - startTime) / 60000); // время в минутах
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [startTime]);

  useEffect(() => {
    if (input.length > 0 && startTime) {
      const wordsTyped = input.length / 5;
      setWpm(Math.round(wordsTyped / timeElapsed));
      setErrors(input.split('').filter((char, index) => char !== text[index]).length);
    }
  }, [input, timeElapsed]);

  return { wpm, errors, timeElapsed };
};

export default useTypingSpeed;
