import { useState, useEffect, useRef } from "react";
import TypingText from "../components/TypingText";
import InputField from "../components/InputField";
import Results from "../components/Results";

// Массив текстов для выбора
const texts = [
  `the quick brown fox jumps over the lazy dog repeatedly without ever stopping to rest or slow down it continues to leap across the vast open field as the sun sets in the distance casting long shadows on the ground`,
  `a journey of a thousand miles begins with a single step each step you take brings you closer to your destination and though the path may be long and winding persistence will ultimately lead you to your goal`,
  `all work and no play makes jack a dull boy but if jack balances his time between work and play he will find that he is both productive and happy ready to take on any challenge that comes his way
  `,
  `to be or not to be that is the question whether tis nobler in the mind to suffer the slings and arrows of outrageous fortune or to take arms against a sea of troubles and by opposing end them`,
  `practice makes perfect so keep on typing even when your fingers are tired or your mind starts to wander the more you practice the faster and more accurate you will become improving your skills every day`
];

const Home = () => {
  const [isCompleted, setIsCompleted] = useState(false);
  const [startTime, setStartTime] = useState(null);
  const [time, setTime] = useState(30);
  const [wpm, setWpm] = useState(0);
  const [errors, setErrors] = useState(0);
  const [input, setInput] = useState('');
  const [currentText, setCurrentText] = useState(texts[0]); // Текущий текст
  const inputRef = useRef(null);

  const handleChange = (e) => {
    const value = e.target.value;
    const lastChar = value[value.length - 1];
    const lastCharInInput = input[input.length - 1];

    if (!startTime) {
      setStartTime(Date.now());
    }
    if (isCompleted) {
      return; // Блокируем дальнейший ввод после завершения
    }
    // Условие для игнорирования пробела внутри слова
    if (lastChar === ' ' && lastCharInInput !== ' ' && texts[input.length] !== ' ') {
    return;
    }
    setInput(e.target.value);
  };

  const resetGame = () => {
    setInput('');
    setStartTime(null);
    setIsCompleted(false);
    setCurrentText(texts[Math.floor(Math.random() * texts.length)]); // Выбор нового текста
    if (inputRef.current) {
      inputRef.current.focus();
    }
  };

  

  useEffect(() => {
    if (startTime) {
      const interval = setInterval(() => {
        const time = (Date.now() - startTime) / 1000; // время в секундах
        setTime(prev => prev - 1);
        if (time >= 30) {
          setIsCompleted(true);
          clearInterval(interval);
        }
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [startTime]);

  useEffect(() => {
    if (isCompleted) {
      if (inputRef.current) {
        inputRef.current.blur(); // Убираем фокус с поля ввода
      }
      const wordsTyped = input.trim().split(/\s+/).length; // Подсчет слов
      setWpm(Math.round((wordsTyped / 30) * 60)); // Используем 30 секунд
      setErrors(input.split('').filter((char, index) => char !== currentText[index]).length);
    } else if (input.length > 0 && startTime) {
      // Подсчет ошибок в реальном времени
      setErrors(input.split('').filter((char, index) => char !== currentText[index]).length);
    }
  }, [input, isCompleted, startTime, currentText]);

  useEffect(() => {
    if (input.length === currentText.length) {
      setIsCompleted(true);
      if (inputRef.current) {
        inputRef.current.blur(); // Убираем фокус с поля ввода при завершении
      }
    }
  }, [input, currentText]);

  // Обработчик кликов по документу для установки фокуса на input, если тест не завершен
  useEffect(() => {
    const handleDocumentClick = () => {
      if (inputRef.current && !isCompleted) {
        inputRef.current.focus();
      }
    };
    document.addEventListener('click', handleDocumentClick);
    return () => {
      document.removeEventListener('click', handleDocumentClick);
    };
  }, [isCompleted]);

  return (
    <div className="flex flex-col items-center gap-y-[50px]">
      <h1>Typing Speed Trainer</h1>
      <TypingText text={currentText} input={input} />
      <InputField value={input} onChange={handleChange} inputRef={inputRef} isDisabled={isCompleted} />
      <p>{Math.round(time)}</p>
      {isCompleted && <Results wpm={wpm} errors={errors} resetGame={resetGame} />}
      <button className="px-[12px] py-[7px] bg-slate-700 rounded-lg" onClick={resetGame}>Перезапустить</button> {/* Кнопка для перезапуска */}
    </div>
  );
};

export default Home;
