import React, { useState, useEffect } from "react";
import TypingText from "../components/TypingText";
import InputField from "../components/InputField";
import Results from "../components/Results";

const texts = [
  `this is a typing test you need to type correctly`,
  `react is a library for building user interfaces`,
  `practice makes perfect keep typing and improving`,
];

const Home = () => {
  const [isCompleted, setIsCompleted] = useState(false);
  const [startTime, setStartTime] = useState(null);
  const [input, setInput] = useState('');
  const [currentText, setCurrentText] = useState(texts[0]);

  const handleChange = (e) => {
    const value = e.target.value;
    const lastChar = value[value.length - 1];
    
    // Не позволять ввод символов после завершения текста
    if (isCompleted) return;

    // Ограничить ввод пробела
    if (lastChar === ' ') {
      const words = value.trim().split(' ');
      const currentWord = words[words.length - 1];
      const expectedWord = currentText.split(' ')[words.length - 1];

      // Разрешить ввод пробела только если текущее слово введено правильно
      if (currentWord !== expectedWord) return;
    }

    // Обновляем ввод и стартуем таймер при первом вводе
    if (!startTime && value.length > 0) {
      setStartTime(Date.now());
    }
    setInput(value);

    // Проверка на завершение ввода текста
    if (value === currentText) {
      setIsCompleted(true);
    }
  };

  const resetGame = () => {
    setInput('');
    setStartTime(null);
    setIsCompleted(false);
    setCurrentText(texts[Math.floor(Math.random() * texts.length)]);
  };

  return (
    <div className="flex flex-col items-center gap-y-[50px]">
      <h1>Typing Speed Trainer</h1>
      <TypingText text={currentText} input={input} />
      <InputField value={input} onChange={handleChange} />
      {isCompleted && <Results />}
      <button className="px-[12px] py-[7px] bg-slate-700 rounded-lg" onClick={resetGame}>
        Перезапустить
      </button>
    </div>
  );
};

export default Home;
